function getTlvData(rawObj, mapObj){
  let info = {}
  let splitPos = 0
  let endPos = rawObj.length
  let typeLen = mapObj.typelen !== undefined ? mapObj.typelen : 2
  let lenLen = mapObj.lenLen !== undefined ? mapObj.lenLen : 2
  let idx = 1
  let typeName = ''
  let lenName = ''
  let start = 0
  let end = 0
  let dataType = ''
  while(splitPos <= endPos){
    typeName = 'type'+idx
    lenName = 'len'+idx
    start = Number(splitPos)
    end = Number(start) + Number(typeLen)
    //split type
    let subStr = rawObj.substring(start, end)
    let data = parseInt(subStr, 16)
    dataType = 'type'+data
    node.warn('st:'+start+' end:'+end+' subStr:'+subStr+' data:'+data)
    info['type']=data
    info[typeName] = data
    start = end
    end = start + Number(lenLen)
    //split len
    subStr = rawObj.substring(start, end)
    data = parseInt(subStr, 16)
    node.warn('st:'+start+' end:'+end+' subStr:'+subStr+' data:'+data)
    info[lenName] = data
    start = end
    end = start + data*2
    subStr = rawObj.substring(start, end)
    //check data type
    let valObj = mapObj[dataType]
    switch(valObj.valType){
      case 'ascii':
        data = transferASCII(subStr)  
        break
      case 'dec':
        data = parseInt(subStr)
        break
      case 'str':
        data = subStr
        break
      default:
        data = parseInt(subStr, 16)
    }
    node.warn('st:'+start+' end:'+end+' subStr:'+subStr+' data:'+data)
    info[valObj.valName] = data
    splitPos = end
    idx++
  }
  return info
}
function getMapData(rawObj, mapObj){
