  if (diff === 'str') {
    return subStr
  } else if (diff==='dec') {
    return parseInt(subStr)
  }else if (diff==='signhex') {
    return parseSignHex(subStr)
  } else {
  let data = parseInt(subStr, 16)
  let result = eval(diff)
    return Number(result.toFixed(2))
  }
}

function parseSignHex(hex) {
  if (hex.length % 2 != 0) {
    hex = "0" + hex
  }
  let num = parseInt(hex, 16)
  let maxVal = Math.pow(2, hex.length / 2 * 8);
  if (num > maxVal / 2 - 1) {
    num = num - maxVal
  }
  return num
}
