function parseHex(str, len){
  let bi = parseInt(str).toString(16)
  let fillStr = '0'
  for(let i = 0 ; i < len ; i++){
    fillStr = fillStr + '0'
  }
  len = len * -1
  bi = (fillStr+bi).slice(len)
  return bi
