function reverseStr(str){
  let cnt = str.length / 2
  var bi = ''
  var tmp = []
  for(let i = 0 ; i < cnt ; i++){
    let st = i*2
    var pop = str.substr(st, 2)
    tmp.push(pop)
  }
  for(let j = tmp.length -1 ; j >= 0 ; j--){
    bi = bi+tmp[j]
  }
  return bi
}
